from Crypto.Util.number import long_to_bytes, inverse
import math
import sympy.ntheory as nt


def retrieve_p_q(N: int, e: int, d: int):
    k = d*e-1
    g = 2
    while True:
        t = k
        # g=random.randint(2,N-1) Use small primes instead
        while (t % 2 == 0):
            t //= 2
            x = pow(g, int(t), N)
            y = math.gcd(x-1, N)
            if (x > 1 and y > 1):
                p = y
                q = N//y
                return p, q
        g = nt.nextprime(g)


